import React, { Fragment, useState } from "react";

    

const InputDepdencia=()=>{
    const [id, setID_Dependencia] = useState (0);

    const [nombre, setNombre] = useState ("Aqui va el texto default 2");

    const [centro_costo, setCentroCosto] = useState ("Aqui va el texto default 3");

    const onSubmitForm = async (e) =>{
        e.preventDefault();
        console.log('onSummit')
        try {
            const body = {id,nombre,centro_costo };
            
            const response = await fetch("http://localhost:5000/dependencias",{
                method: "POST",
                headers : { "Content-type": "application/json" },
                body: JSON.stringify(body)

            });
            window.location = "/";
    
        } catch (err) {
            console.error(err.message);
        }
    }

    return (
        <Fragment>
            <h1 className= "text-center mt-5">
                Dependencia List
                
                <form className= "d-flex mt-5" onSubmit={ onSubmitForm }>
                    <input 
                        type= "intenger" 
                        className= "form-control" 
                        value ={id}
                        onChange ={e => setID_Dependencia (parseInt(e.target.value))}
                    />

                    <input 
                        type= "text" 
                        className= "form-control" 
                        value ={Nombre}
                        onChange ={e => setNombre(e.target.value)}
                    />

                    <input 
                        type= "text" 
                        className= "form-control" 
                        value ={CentroCosto}
                        onChange ={e => setCentroCosto (e.target.value)}
                    />

                    <button className="btn btn-success">
                        Add
                    </button>
                </form>

             
            </h1>
            </Fragment>
        );
};

export default InputDepdencia;
