import React, { Fragment, useState } from 'react';

const EditDependencia=({dependencia})=>{
    


    const [Nombre, setNombre] = useState (dependencia.nombre);

    const [CentroCosto, setCentroCosto] = useState (dependencia.Centro_Costo);
    
    const updateDependencias = async (e) => {
        e.preventDefault();
        try {
            const body = { Nombre,CentroCosto };
            const response = await fetch(
            `http://localhost:5000/dependencias/${dependencia.id}`,
            {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body)
            }
        );

            window.location = "/";
        } catch (err) {
            console.error(err.message);
        }
    };
   return(
    <Fragment>
        <button type="button" 
            class="btn btn-warning" 
            data-toggle="modal" 
            data-target={`#id${dependencia.id}`}
        >

            CUCA
        </button>

        <div class="modal"  
                    onClick= {() => setNombre(dependencia.Nombre)}>
        <div class="modal-dialog">
            <div class="modal-content">
            <div class="modal-header">
                <h4 class="modal-title">
                    Edit Dependencia
                </h4>
                <button 
                    type="button" 
                    class="close" 
                    data-dismiss="modal" 
                    onClick= {() => setNombre(dependencia.Nombre)}
                    >
                        &times;
                </button>
            </div>

            <div class="modal-body">
                <input 
                type= "text" 
                className="form-control" 
                value ={ Nombre } 
                onChange= { e => setNombre(e.target.value)}
               />
            </div>

            <div class="modal-footer">
                <button 
                    type="button" 
                    class="btn btn-warning" 
                    data-dismiss="modal"
                    
                    >    
                    Edit
                </button>
                <button 
                    type="button" 
                    class="btn btn-danger" 
                    data-dismiss="modal" 
                    onClick= {() => setNombre(dependencia.Nombre)}
                   >
                        Close
                </button>
            </div>

            </div>
        </div>
        </div>  
    </Fragment>
   );
};

export default EditDependencia
