import React, { useState, useEffect } from 'react'
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'
import FormControl from '@material-ui/core/FormControl'
import RadioGroup from '@material-ui/core/RadioGroup'
import Radio from '@material-ui/core/Radio'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import InputLabel from '@material-ui/core/InputLabel'
import Select from '@material-ui/core/Select'
import MenuItem from '@material-ui/core/MenuItem'
import useForm from '../useForm/useForm'
import Proveedores from '../Proveedores/Proveedores'

// id_Linea, rif 
const LineaProveedorForm = () => {
    const [lineas, setLineas] = useState([{}])
    const [proveedores, setProveedores] = useState([{}])
    const [toggle, setToggle] = useState(false)
    const proxy = 'trabajadores'
    const { handleChange, handleSubmit, values } = useForm({
        id_linea: '',
        rif: '',
    }, proxy)

    const toggleSelect = ({ target }) => setToggle(target.value == "" ? true : false)

    const fetchLineas = () => {
        fetch('http://localhost:5000/linea_suministro')
            .then(res => res.json())
            .then(result => setLineas(result))
            .catch(err => console.log(err.message))
    }
    const fetchProveedores = () => {
        fetch('http://localhost:5000/proveedores')
            .then(res => res.json())
            .then(result => setProveedores(result))
            .catch(err => console.log(err.message))
    }


    useEffect(() => {
        fetchLineas()
        fetchProveedores()
    }, [])

    return (
        <div className="form-container">
            <form onSubmit={handleSubmit}>
                <FormControl className="form-trabajador">
                    <div className="form-trabajador-1">    
                        <FormControl>
                            <InputLabel id="Lineas-label">Linea</InputLabel>
                            <Select
                                labelId="lineas-label"
                                id="lineas"
                                value={values.id_linea}
                                name="id_linea"
                                onChange={handleChange}
                                onBlur={handleChange}
                                >
                                {lineas.map((linea, i) => (
                                    <MenuItem value={linea.id} key={i}>
                                        {linea.nombre}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                    </div>
                    
                    <Button type="submit" variant="contained" size="small" disableElevation>Añadir Proveedor</Button>
                </FormControl>
            </form>
        </div>
    )
}

export default LineaProveedorForm
