import React, { useState, useEffect } from 'react'
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'
import FormControl from '@material-ui/core/FormControl'
import RadioGroup from '@material-ui/core/RadioGroup'
import Radio from '@material-ui/core/Radio'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import InputLabel from '@material-ui/core/InputLabel'
import Select from '@material-ui/core/Select'
import MenuItem from '@material-ui/core/MenuItem'
import useForm from '../useForm/useForm'

// cedula, id_dependencia, nombre, tipo 
const CotizacionesForm = () => {

    const [req, setReq] = useState([{}])
    const [linea, setlinea] = useState([{}])

    const [toggle, setToggle] = useState(false)
    
    const proxy = 'cotizacion'
    const { handleChange, handleSubmit, values } = useForm({
        id: '',
        id_linea: '',
        fecha_emision: getLocalDate(),
        observaciones: '',
        condiciones_entrega:'',
        id_requisicion: '',
        //id_cotizacion: ''
    }, proxy)



    const toggleSelect = ({ target }) => setToggle(target.value == "" ? true : false)

    const fetchLineas = () => {
        fetch('http://localhost:5000/lineas_suministro')
            .then(res => res.json())
            .then(result => setlinea(result))
            .catch(err => console.log(err.message))
    }

    const fetchRequisiciones = () => {
        fetch('http://localhost:5000/requisiciones')
            .then(res => res.json())
            .then(result => setReq(result))
            .catch(err => console.log(err.message))
    }

    function getLocalDate() {
        var today = new Date();
        var dd = String(today.getDate()).padStart(2, '0');
        var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
        var yyyy = today.getFullYear();
        today = mm + '-' + dd + '-' + yyyy;
        return today;
    }

    useEffect(() => {
        fetchLineas()
        fetchRequisiciones()
    }, [])

    return (
        <div className="form-container">
            <form onSubmit={handleSubmit}>
                <FormControl className="form-trabajador">
                    <div className="form-trabajador-1">
                        <TextField
                            className="text-field"
                            size="small"
                            label="ID"
                            name="id"
                            variant="outlined"
                            value={values.id}
                            onChange={handleChange} />
                        <TextField
                            className="text-field"
                            size="small"
                            label="Fecha de Emision"
                            name="fecha_emision"
                            variant="outlined"
                            value={getLocalDate()} />
                    </div>
                    <TextField
                        className="text-field"
                        size="small"
                        label="Observaciones"
                        name="observaciones"
                        variant="outlined"
                        value={values.observaciones}
                        onChange={handleChange} />
                   
                    <FormControl>
                        <InputLabel id="requisicion-label">Requisicion</InputLabel>
                        <Select
                            labelId="requisicion-label"
                            id="requisicion"
                            value={values.id_requisicion}
                            name="id_requisicion"
                            onChange={handleChange}
                            onBlur={handleChange}
                        >
                            {req.map((req, i) => (
                                <MenuItem value={req.id} key={i}>
                                    {req.id}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                    <FormControl>
                        <InputLabel id="lineas-label">Linea</InputLabel>
                        <Select
                            labelId="lineas-label"
                            id="linea"
                            value={values.id_linea}
                            name="id_linea"
                            onChange={handleChange}
                            onBlur={handleChange}
                        >
                            {linea.map((lineas, i) => (
                                <MenuItem value={lineas.id} key={i}>
                                    {lineas.descripcion}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                    {/* {toggle ?
                        <FormControl className="instituciones">
                            <InputLabel id="instituciones-label">Instituciones</InputLabel>
                            <Select
                                labelId="instituciones-label"
                                id="instituciones"
                                value={values.cod_inst}
                                name="cod_inst"
                                onChange={handleChange}
                                onBlur={handleChange}
                            >
                                {instituciones.map((institucion, i) => (
                                    <MenuItem value={institucion.cod_inst} key={i}>
                                        {institucion.nombre_inst}
                                    </MenuItem>
                                ))}
                            </Select>
                        </FormControl>
                        : <div></div>
                    } */}
                    <Button type="submit" variant="contained" size="small" disableElevation>Añadir Cotizacion</Button>
                </FormControl>
            </form>
        </div>
    )
}

export default CotizacionesForm
